// Match based on body
def requestBody = mockRequest.getRequestContent()
log.info "Request body: " + requestBody

if( !(requestBody.contains("email_address") && requestBody.contains("birth_date") && requestBody.contains("email_opt_in") ))
{
    // return the name of the response you want to dispatch
    return "422"
}

if( requestBody.contains("231100") )
{
    // return the name of the response you want to dispatch
    return "200 true"
}

if( requestBody.contains("231101") )
{
    // return the name of the response you want to dispatch
    return "200 false"
}

if( !requestBody.contains("pers_id") && requestBody.contains("331100"))
{
    // return the name of the response you want to dispatch
    return "201 true"
}

if( !requestBody.contains("pers_id") && requestBody.contains("331101"))
{
    // return the name of the response you want to dispatch
    return "201 false"
}

if( !(requestBody.contains("231100") || requestBody.contains("231101") || requestBody.contains("331100") || requestBody.contains("331101") ))
{
    // return the name of the response you want to dispatch
    return "404"
}

